Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Arnoldki – wyciskanie hantli nad głowę z rotacją',CONCAT('1) Pozycja siedząca, kręgosłup dociśnięty do oparcia ławeczki, stopy mocno wciśnięte w podłoże. ',CHAR(10),'2) Hantle przed sobą na wysokości górnej części klatki. Palce odwrócone do klatki piersiowej, kciuki na zewnątrz. ',CHAR(10),'3) Ramiona blisko tułowia.'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową, unoś hantle nad głowę, odkręcając nadgarstek tak, aby palce były skierowane do przodu. Wykonuj wydech w momencie, kiedy ramiona są w pełni wyprostowane nad głową, a następnie wdech. ',CHAR(10),'2) Po krótkim przytrzymaniu hantli nad głową i pełnym dopięciu mięśni naramiennych powolnym ruchem opuszczaj hantle w dół, odwracając nadgarstek tak, aby palce były skierowane w stronę klatki piersiowej. Wykonaj również płynny wdech. ',CHAR(10),'3)Wykonaj wyznaczoną liczbę powtórzeń.'),CONCAT('1) Stopy mocno wciśnij w podłogę. ',CHAR(10),'2) Trzymaj głowę w równej linii z plecami. ',CHAR(10),'3) Bądź skoncentrowany.'),1,'arnoldki_wyciskanie_hantli_nad_glowe_z_rotacja.mp4',8, '/na-barki/Arnoldki_–_wyciskanie_hantli_nad_glowe_z_rotacja.jpg');
