Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Naprzemianstronne unoszenie ramion w przód ze sztangielkami',CONCAT('1) Pozycja stojąca, ramiona wyprostowane wzdłuż tułowia, hantle w dłoniach (kciuki skierowane do środka). ',CHAR(10),'2) Stopy w lekkim rozkroku. Plecy proste.'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową i unikając kołysania tułowia, wykonaj wdech i wznieś sztangielkę w przód, aż ramiona będą ustawione równolegle do podłogi. ',CHAR(10),'2) Wykonaj wydech, po czym opuść ramię. ',CHAR(10),'3) Wykonaj powtórzenie drugą ręką. ',CHAR(10),'4)Wykonaj wyznaczoną liczbę powtórzeń.'),CONCAT('1) Tempo: 1 sekunda w górę, 2 sekundy w dół. ',CHAR(10),'2) Utrzymuj stałe napięcie mięśni. ',CHAR(10),'3) Nie szarp ciężaru. Pamiętaj o spokojnym odłożeniu ciężaru. ',CHAR(10),'4) Trzymaj głowę w równej linii z plecami. ',CHAR(10),'5) Bądź skoncentrowany.'),1,'naprzemienne_unoszenie_ramion_w_przod_ze_sztangielkami.mp4',8, '/na-barki/Naprzemianstronne_unoszenie_ramion_w_przod_ze_sztangielkami.jpg');
