Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Unoszenie ramion w przód ze sztangielką trzymaną oburącz',CONCAT('1) Stojąca, ramiona wyprostowane wzdłuż tułowia, trzymają sztangielkę chwytem neutralnym. ',CHAR(10),'2) Stopy w lekkim rozkroku. Plecy proste.'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową i unikając kołysania tułowiem, wykonaj wdech i wznieś sztangielkę w przód przed siebie, aż dłonie będą znajdować się nieco wyżej niż barki. ',CHAR(10),'2) Wykonaj wydech, po czym opuszczaj ramiona w dół. ',CHAR(10),'3)Wykonaj wyznaczoną liczbę powtórzeń.'),CONCAT('1) Tempo: 1 sekunda w górę, 2 sekundy w dół. ',CHAR(10),'2) Utrzymuj stałe napięcie mięśni. ',CHAR(10),'3) Nie kołysz tułowiem. Pamiętaj o spokojnym odłożeniu ciężaru. ',CHAR(10),'4) Trzymaj głowę w równej linii z plecami. ',CHAR(10),'5) Bądź skoncentrowany.'),1,'unoszenie_ramion_w_przod_ze_sztangielka_trzymana_oburacz.mp4',8, '/na-barki/Unoszenie_ramion_w_przod_ze_sztangielka_trzymana_oburacz.jpg');
