Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Wyciskanie sztangi nad głowę',CONCAT('1) Złap sztangę nachwytem zamkniętym nieco szerzej niż na szerokość barków.',CHAR(10),'2) Stopy w lekkim rozkroku. Plecy proste, brzuch napięty.',CHAR(10),'3) Sztangę utrzymuj na wysokości obojczyków.'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową, wykonaj wdech i rozpocznij wyciskanie ciężaru w górę. Łokcie prowadź przodem, przedramiona staraj się utrzymać na stałe skierowane w górę. ',CHAR(10),'2) Po wyciśnięciu wykonaj wydech, nie blokuj rąk w stawach łokciowych – nie dopuszczaj do ich przeprostu.',CHAR(10),'3) Następnie wykonaj wdech oraz opuść sztangę.',CHAR(10),'4) Powróć do pozycji wyjściowej, opuszczając sztangę na linię obojczyków.',CHAR(10),'5) Wykonaj ruch zadaną liczbę powtórzeń.'),CONCAT('1) Utrzymaj stałe napięcie ćwiczonych mięśni.',CHAR(10),'2) Ustabilizuj sylwetkę, napinając mięśnie brzucha oraz pośladki.',CHAR(10),'3) Nie wybijaj ciężaru z nóg.',CHAR(10),'4) Bądź skoncentrowany.'),2,'wyciskanie_sztangi_nad_glowe.mp4',8, '/na-barki/Wyciskanie_sztangi_nad_glowe.jpg');
