Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Wyciskanie hantli nad głowę siedząc',CONCAT('1) Siedzącana ławce. Plecy dociśnięte do oparcia. Nogi mocno zapierają się o podłogę. ',CHAR(10),'2) Unieś ramiona w górę tak, aby hantle znajdowały się na wysokości głowy. Palce zwrócone są do przodu. Łokcie ugięte.'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową, wyciskaj hantle nad głowę, prostując ramiona w łokciach i wykonaj wydech.  odbywa się po trójkącie, tzn. hantle schodzą się do środka w momencie, gdy są nad głową. ',CHAR(10),'2) Następnie powolnym ruchem opuszczaj hantle, wykonując również płynny wdech. ',CHAR(10),'3)Wykonaj wyznaczoną liczbę powtórzeń.'),CONCAT('1) Stopy mocno wciśnij w podłogę. ',CHAR(10),'2) Łokieć znajduje się pod ciężarem podczas wykonywania ćwiczenia. ',CHAR(10),'3) Trzymaj głowę w równej linii z plecami. ',CHAR(10),'4) Bądź skoncentrowany.'),2,'wyciskanie_hantli_nad_glowe_siedzac.mp4',8, '/na-barki/Wyciskanie_hantli_nad_glowe_siedzac.jpg');
