Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Landmine twist',CONCAT('1) Pozycja stojąca, plecy proste. ',CHAR(10),'2) Koniec sztangi trzymany w wyprostowanych ramionach nad głową. ',CHAR(10),'3) Klatka piersiowa wypchnięta, łopatki ściągnięte,mięśnie brzucha i mięśnie pośladkowe napięte.'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową, weź wdech i wykonaj powolną rotację ciała, równocześnie opuszczaj sztangę w kierunku biodra. ',CHAR(10),'2) Podczas ruchu utrzymuj mocno napięte mięśnie, ruch opuszczania wykonuj powoli, z dużą kontrolą. ',CHAR(10),'3) Następnie energicznie unieś sztangę do pozycji początkowej, jednocześnie wykonaj wydech.'),CONCAT('1) Postaraj się utrzymać proste plecy. ',CHAR(10),'2) Ćwiczenie wykonuj powoli, kontroluj cały przebieg ruchu. ',CHAR(10),'3) Wzrok kieruj za sztangą.'),1,'video-landmine-twist.mp4',2, '/na-brzuch/Landmine_twist.jpg');
