Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Janda sit up',CONCAT('1) Pozycja leżąca, kończyny dolne ugięte. ',CHAR(10),'2) Guma zaczepiona z jednej strony powyżej kolan, z drugiej pod stopami.  ',CHAR(10),'3) Ramiona wyprostowane za głową.'),CONCAT('1) Weź wdech, napnij mięśnie brzucha i oderwij odcinek piersiowy kręgosłupa od podłogi, cały czas dociskaj stopy do podłogi. ',CHAR(10),'2) Wydychając powietrze, wróć do pozycji wyjściowej.'),CONCAT('1) Mięśnie brzucha powinny być mocno napięte przez cały czas trwania ćwiczenia.  ',CHAR(10),'2) Pilnuj, aby nie odrywać stóp od podłogi. Jeżeli jest to niemożliwe, zmniejsz napięcie gumy.  ',CHAR(10),'3) Skup się podczas wykonywania ruchu.  ',CHAR(10),'4) Zwróć uwagę na prawidłowe oddychanie.'),1,'video-janda-situp.mp4',2, '/na-brzuch/Janda_sit_up.jpg');
