Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Superman na TRX',CONCAT('1) Podpór przodem na taśmach TRX.',CHAR(10),'2) Tułów wyprostowany, ręce ustawione prostopadle do osi ciała, dłonie w jednej linii ustawione równolegle do siebie, nieco szerzej niż rozstaw barków. ',CHAR(10),'3) Nogi wyprostowane, ciało w jednej linii.',CHAR(10),'4) Stopy mocno zaparte o podłoże.'),CONCAT('1) Przed wykonaniem ruchu weź powietrze do przepony i napnij pośladki. ',CHAR(10),'2) Zachowując naturalną krzywiznę kręgosłupa, zacznij powoli wysuwać piłkę w przód. ',CHAR(10),'3) Piłkę docelowo wysuń tak daleko, by ciało znalazło się niemalże równolegle do podłoża. ',CHAR(10),'4) Utrzymując proste plecy, wróć do pozycji wyjściowej.'),CONCAT('1) W momencie odczuwania dyskomfortu w kolanach podłóż pod nie miękki przedmiot.',CHAR(10),'2) Ćwiczenie wymaga utrzymania stałego napięcia mięśni brzucha oraz pośladków.',CHAR(10),'3) Ćwiczenie wymaga zachowania neutralnej pozycji kręgosłupa. ',CHAR(10),'4) Nie przeginaj odcinka lędźwiowego.',CHAR(10),'5) Nie wykonuj tego ćwiczenia w przypadku bólu i dyskomfortu w odcinku lędźwiowym.'),1,'superman_trx.mp4',2, '/na-brzuch/Superman_na_TRX.jpg');
