Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Rewersy',CONCAT('1) Pozycja leżąca na plecach, na macie. Ramiona proste, ustawione wzdłuż tułowia.',CHAR(10),'2) Kończyny dolne uniesione z podłoża, ugięte w kolanach.',CHAR(10),'3) Odcinek lędźwiowy kręgosłupa dotyka maty.'),CONCAT('1)Utrzymując prawidłową pozycję wyjściową, przyciągaj dynamicznie na zmianę raz jedno, raz drugie kolano do klatki piersiowej.'),CONCAT('1) Kontroluj ruch podczas trwania ćwiczenia.',CHAR(10),'2) Kolana przyciągaj na przemian. Stopy nie spotykają się z tyłu.',CHAR(10),'3) Napinając mięśnie brzucha oraz pośladkowe, zachowuj prawidłową (wyprostowaną) postawę.',CHAR(10),'4) Ćwiczenie wykonuj dynamicznie, ale staraj się kontrolować ruch.'),1,'reversy.mp4',2, '/na-brzuch/Rewersy.jpg');
