Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Dead bug – nogi proste',CONCAT('1) Pozycja leżąca na plecach.',CHAR(10),'2) Nogi lekko ugięte w kolanach lub proste, ramiona wyprostowane, skierowane przed siebie.'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową, wykonaj mocny wdech do brzucha, następnie powolnym i kontrolowanym ruchem rozpocznij unoszenie ramion przodem z jednoczesnym pochylaniem ciała w przód.',CHAR(10),'2)  kontynuuj do momentu, w którym będziesz w stanie utrzymać ciało w pełnym wyproście, lub w którym ramiona znajdą się w linii ciała.',CHAR(10),'3) Spokojnym ruchem wraz z wydechem wróć do pozycji wyjściowej poprzez przywodzenie ramion.'),CONCAT('1) Kontroluj ruch podczas trwania ćwiczenia.',CHAR(10),'2) Bądź skoncentrowany.',CHAR(10),'3) Bardzo mocne napięcie mięśni brzucha i pośladków pozwoli zachować prawidłową (wyprostowaną) postawę.',CHAR(10),'4) Zbyt szybkie wykonywanie ćwiczenia jest błędem technicznym.'),1,'dead_bug_nogi_proste.mp4',2, '/na-brzuch/Dead_bug_–_nogi_proste.jpg');
