Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Dead bug – nogi ugięte',CONCAT('1) Pozycja leżąca na plechach,odcinek lędźwiowy kręgosłupa przylega do podłoża.',CHAR(10),'2)Nogi ugięte w stawach biodrowych i kolanowych pod kątem 90 stopni, ramiona wyprostowane, skierowane ku górze, stopy zgięte grzbietowo. ',CHAR(10),'3)Brzuch i pośladki napięte.'),CONCAT('1) Oprzyj przedramię o podłogę, unieś kolana i przekręć ciało tak, aby ustawić się bokiem, równolegle do podłogi. ',CHAR(10),'2) Utrzymuj sztywno wyprostowaną pozycję, drugą rękę skieruj w górę lub ułóż na biodrze.'),CONCAT('1) Mięśnie brzucha powinny być mocno napięte, całe ciało w jednej linii.',CHAR(10),'2) Dolny odcinek pleców nie może być wygięty. ',CHAR(10),'3) W celu zwiększenia intensywności ćwiczenia możesz wykonać je z zaczepioną jedną nogą.'),1,'dead_bug_nogi_ugiete.mp4',2, '/na-brzuch/Dead_bug_–_nogi_ugiete.jpg');
