Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Hollow body',CONCAT('1)i ćwiczenie Z leżenia tyłem wykonaj równoczesny wznos prostych ramioni nóg. Pozycja ciała w trakcie spięcia powinna przypominać lekki łuk. Pozycję utrzymaj przez zakładany czas. Podczas spięcia odcinek lędźwiowy kręgosłupa powinien być mocno dociśnięty do podłoża.'),CONCAT('1) W leżeniu poprzez spięcie mięśni brzucha przyciągnij kolana do klatki piersiowej. ',CHAR(10),'2)  kontynuuj do momentu, aż biodra oderwą się od podłogi.',CHAR(10),'3) Przez ułamek sekundy przytrzymaj ruch w maksymalnym napięciu mięśni brzucha.',CHAR(10),'4) Następnie powoli powróć do pozycji początkowej.'),CONCAT('1) Mięśnie brzucha powinny być mocno napięte przez cały czas trwania ćwiczenia.',CHAR(10),'2) Ćwiczenie wykonuj powoli, z pełnym wyczuciem zaangażowanej grupy mięśniowej.',CHAR(10),'3) Skup się podczas wykonywania ruchu.',CHAR(10),'4) Postaraj się skoncentrować napięcie na mięśniach brzucha.'),1,'hollow_body_gora_dol.mp4',2, '/na-brzuch/Hollow_body.jpg');
