Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Naprzemienne sięganie do kostek leżąc',CONCAT('1) Pozycja leżąca, kończyny dolne ugięte, stopy na podłodze rozstawione na szerokość barków. ',CHAR(10),'2) Ramiona wyprostowane spoczywają wzdłuż tułowia. Broda przyklejona do klatki piersiowej.'),CONCAT('1) Z pozycji wyjściowej wykonaj rotację tułowia w stronę przeciwną do wyciągu.',CHAR(10),'2) W trakcie ruchu wykonaj wydech, utrzymuj ramiona wyprostowane oraz biodra w pozycji na wprost ze stopami. ',CHAR(10),'3) W momencie maksymalnej rotacji utrzymaj pozycję. ',CHAR(10),'4) Wykonując wdech, rozpocznij spokojny kontrolowany powrót do pozycji wyjściowej.'),CONCAT('1) Ramiona trzymaj wyprostowane cały czas na wysokości barków, łopatki dociśnij do siebie.',CHAR(10),'2) Ważne jest, aby w tym ćwiczeniu starać się utrzymywać biodra w jednej linii ze stopami.',CHAR(10),'3) Podczas wykonywania ruchu mięśnie brzucha trzymaj w stałym napięciu. ',CHAR(10),'4) Utrzymuj ciało w pozycji pionowej bez odchyleń na boki lub w linii przód–tył. ',CHAR(10),'5) Pamiętaj o odpowiednim oddechu.',CHAR(10),'6) Nie śpiesz się podczas wykonywania tego ćwiczenia, zbyt szybkie wykonanie jest błędem technicznym.'),1,'naprzemienne_sieganie_do_kostek_lezac.mp4',2, '/na-brzuch/Naprzemienne_sieganie_do_kostek_lezac.jpg');
