Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Przyciąganie kolan do klatki w zwisie na drążku',CONCAT('1) Drążek złapany nachwytem (kciuki na zewnątrz) na szerokość barków.',CHAR(10),'2) Tułów w jednej linii z wyprostowanymi kończynami dolnymi, miednica ustawiona neutralnie.'),CONCAT('1) Z pozycji wyjściowej wykonaj rotację tułowia w stronę przeciwną do wyciągu (w skos–górę).',CHAR(10),'2) W trakcie ruchu wykonaj wydech, utrzymuj ramiona wyprostowane oraz biodra w pozycji na wprost ze stopami. ',CHAR(10),'3) W momencie maksymalnej rotacji utrzymaj pozycję. ',CHAR(10),'4) Wykonując wdech, rozpocznij spokojny kontrolowany powrót do pozycji wyjściowej.'),CONCAT('1) Utrzymaj wyprostowane ramiona, łopatki dociśnięte do siebie.',CHAR(10),'2) Ważne jest, aby w tym ćwiczeniu starać się utrzymywać biodra w jednej linii ze stopami.',CHAR(10),'3) Podczas wykonywania ruchu mięśnie brzucha trzymaj w stałym napięciu. ',CHAR(10),'4) Utrzymuj ciało w pozycji pionowej bez odchyleń na boki.',CHAR(10),'5) Nie śpiesz się podczas wykonywania tego ćwiczenia, zbyt szybkie wykonanie jest błędem technicznym.'),2,'przyciaganie_kolan_do_klatki_w_zwisie_na_drazku.mp4',2, '/na-brzuch/Przyciaganie_kolan_do_klatki_w_zwisie_na_drazku.jpg');
