Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Unoszenie tułowia z podłoża. Spięcia brzucha leżąc',CONCAT('1)Pozycja leżąca, kończyny dolne ugięte, stopy na podłodze, ręcewzdłuż tułowia.'),CONCAT('1) Napinając mocno mięśnie brzucha, ugnij tułów i unieś część piersiową. ',CHAR(10),'2) Przytrzymaj ruch w maksymalnym napięciu mięśni brzucha przez ułamek sekundy, po czym wykonaj wydech powietrza. ',CHAR(10),'3) Wróć do pozycji wyjściowej, wykonując płynny wdech. ',CHAR(10),'4) Powtórz ugięcie. ',CHAR(10),'5) Wykonaj zadanąliczbę powtórzeń.'),CONCAT('1) Mięśnie brzucha powinny być mocno napięte przez cały czas trwania ćwiczenia. ',CHAR(10),'2) Ćwiczenie wykonuj powoli, z pełnym wyczuciem zaangażowanej grupy mięśniowej. ',CHAR(10),'3) Skup się podczas wykonywania ruchu. ',CHAR(10),'4) Zwróć uwagę na prawidłowe oddychanie. ',CHAR(10),'5) Postaraj się skoncentrować napięcie na mięśniach brzucha, rozluźniając nogi i stopy. ',CHAR(10),'6) Staraj się nie odrywać stóp od podłoża podczas wykonywania spięcia brzucha.'),2,'spiecia_brzucha_lezac_na_macie_ze_zlaczonymi_stopami.mp4',2, '');
