Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Prostowanie ramion z gumą',CONCAT('1) Stań przodem do gumy oporowej zaczepionej wyżej.',CHAR(10),'2) Sylwetka wyprostowana, nieznacznie pochylona.',CHAR(10),'3) Łokcie znajdują się blisko tułowia.'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową, weź wdech i zacznij prostowaćramiona w stawach łokciowych.',CHAR(10),'2) Podczas ruchu utrzymuj ciało nieruchomo, łopatki mocno ściągnięte, kontynuuj ruch do pełnego wyprostu w stawie łokciowym.',CHAR(10),'3) W momencie maksymalnego napięcia zatrzymaj ruch na ułamek sekundy, a następnie zacznij odkładać ciężar, wydychając powietrze.'),CONCAT('1) Kontroluj ruch podczas trwania ćwiczenia.',CHAR(10),'2) Utrzymuj stabilną sylwetkę, ruch ma się odbywać jedynie w stawach łokciowych.',CHAR(10),'3) Bądź skoncentrowany.'),1,'video-prostowanie-ramion-z-guma.mp4',7, '/na-triceps/Prostowanie_ramion_z_guma.jpg');
