Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Prostowanie ramion na TRX',CONCAT('1) Złap uchwyty TRX nachwytem.',CHAR(10),'2) Ramiona w łokciach wyprostowane (bez przeprostów).',CHAR(10),'3) Mięśnie brzucha napięte.',CHAR(10),'4) Ciało lekko pochylone, podpór na taśmach TRX.'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową, zegnij ręce w stawie łokciowym. Schodząc w dół, łokcie również kieruj w dół.',CHAR(10),'2) Opuszczaj ciało, aż czubek głowy znajdzie się przy taśmach TRX. Wyprostuj ramiona w łokciach, zachowując stałe napięcie mięśni.'),CONCAT('1) Podczas wykonywania ruchu utrzymuj wyprostowaną sylwetkę.',CHAR(10),'2) Podczas wykonania ćwiczenia staraj się skupić na pracy tricepsa.',CHAR(10),'3) Bądź skoncentrowany.'),1,'prostowanie ramion_trx.mp4',7, '/na-triceps/Prostowanie_ramion_na_TRX.jpg');
