Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Wyciskanie francuskie z przenoszeniem ramion za głowę',CONCAT('1) Leżąc na ławce, chwyć sztangę łamaną wąskim chwytem. Dłonie nieco węziej niż szerokość barków, kciuki skierowane do siebie.',CHAR(10),'2) Ściągnij sztangę z uchwytu i unieś ją nad klatkę piersiową, ramiona powinny znajdować się prostopadle do podłoża.'),CONCAT('1) Inicjując wdech, opuść sztangę do czoła, trzymając ramiona nieruchomo. Przedramiona wykonują półkolisty ruch.',CHAR(10),'2) Utrzymując stałe zgięcie w łokciu, przenieś sztangę za głowę.',CHAR(10),'3) Następnie przenieś sztangę z powrotem nad czoło i wykonaj wyprost w łokciach, wracając do pozycji początkowej.'),CONCAT('1) Kontroluj ruch podczas trwania ćwiczenia.',CHAR(10),'2) Stopy solidnie zaprzyj o podłogę.',CHAR(10),'3) Bądź skoncentrowany.',CHAR(10),'4) Opuszczanie do czoła wykonuj ze szczególną ostrożnością.'),1,'wyciskanie_francuskie_z_przenoszeniem_ramion_za_glowe.mp4',7, '/na-triceps/Wyciskanie_francuskie_z_przenoszeniem_ramion_za_glowe.jpg');
