Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Wyciskanie sztangi wąskim chwytem',CONCAT('1) Pozycja leżąca na ławeczce płaskiej.',CHAR(10),'2) Sztanga w dłoniach, ramiona wyprostowane nad klatką piersiową.',CHAR(10),'3) Łopatki mocno ściągnięte.'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową, wykonaj wdech, a następnie opuść sztangę do klatki piersiowej na wysokość mostka, utrzymując łokcie blisko ciała.',CHAR(10),'2) Następnie utrzymując tor ruchu jak przy opuszczaniu, wykonaj mocny wyprost w łokciach, wyciskając sztangę nad klatkę piersiową. ',CHAR(10),'3) W końcowej fazie ruchu wykonaj mocny wydech.'),CONCAT('1) Kontroluj ruch podczas trwania ćwiczenia.',CHAR(10),'2) Bądź skoncentrowany.',CHAR(10),'3) Upewnij się, że podczas ruchu prowadzisz łokcie blisko tułowia, tak aby maksymalnie zaangażować mięśnie trójgłowe ramienia.',CHAR(10),'4) Opuszczanie sztangi powinno trwać dwa razy dłużej niż jej wyciskanie.',CHAR(10),'5) Jeżeli wykonujesz to ćwiczenie po raz pierwszy, poproś kogoś o asekurację.'),1,'wyciskanie_sztangi_waskim_chwytem.mp4',7, '/na-triceps/Wyciskanie_sztangi_waskim_chwytem.jpg');
