Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Wyciskanie francuskie hantlami z przenoszeniem ramion za głowę',CONCAT('1) Pozycja leżąca na ławce płaskiej.',CHAR(10),'2) Hantle w dłoniach, ramiona wyprostowane nad klatką piersiową. Kciuki skierowane w tył.'),CONCAT('1) Inicjując wdech, opuść hantle do czoła, trzymając ramiona nieruchomo. Przedramiona wykonują półkolisty ruch.',CHAR(10),'2) Utrzymując stałe zgięcie w łokciu, przenieś hantle za głowę.',CHAR(10),'3) Następnie przenieś hantle z powrotem nad czoło i wykonaj wyprost w łokciach, wracając do pozycji początkowej.',CHAR(10),'4) Wykonaj wyznaczoną liczbę powtórzeń.'),CONCAT('1) Kontroluj ruch podczas trwania ćwiczenia.',CHAR(10),'2) Stopy solidnie zaprzyj o podłogę.',CHAR(10),'3) Bądź skoncentrowany.',CHAR(10),'4) Opuszczanie do czoła wykonuj ze szczególną ostrożnością.'),1,'wyciskanie_fancuskie_hantalmi_z_przenoszeniem_ramion_za_glowe.mp4',7, '/na-triceps/Wyciskanie_francuskie_hantlami_z_przenoszeniem_ramion_za_glowe.jpg');
