Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Wąskie pompki',CONCAT('1) Przyjmij pozycję podporu przodem.',CHAR(10),'2) Dłonie rozstaw na szerokość braków.',CHAR(10),'3) Utrzymuj proste proste plecy.',CHAR(10),'4) Pilnuj, aby łokcie nie znajdowały się w przeproście.'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową, wykonaj dokładne, płynne i powolne opuszczanie klatki piersiowej (bez dotykania nią o podłoże), połączone z wdechem powietrza.',CHAR(10),'2) Następnie wraz z wydechem powietrza dynamiczne wróć tułowiem do góry poprzez mocną pracę ramion.',CHAR(10),'3) Wykonaj wyznaczoną liczbę powtórzeń.'),CONCAT('1) Pamiętaj o prostych plecach i poprawnej pozycji miednicy. ',CHAR(10),'2) Cały czas kontroluj ruch opuszczania tułowia.',CHAR(10),'3) Dynamicznie kieruj tułów do góry.',CHAR(10),'4) Utrzymuj głowę w równej linii z plecami.',CHAR(10),'5) Bądź skoncentrowany.',CHAR(10),'6) Nie spiesz się podczas wykonywania tego ćwiczenia, jego zbyt szybkie wykonanie jest błędem technicznym.'),1,'waskie_pompki.mp4',7, '/na-triceps/Waskie_pompki.jpg');
