Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Prostowanie ramion leżąc na ławce z użyciem wyciągu górnego',CONCAT('1) Połóż się na ławeczce 45 stopni odwróconej tyłem do wyciągu górnego, na którym jest założona lina. ',CHAR(10),'2) Nogi ugnij w kolanach, stopy oprzyj o podłogę. ',CHAR(10),'3) Chwyć linę. ',CHAR(10),'4) Ramiona powinny tworzyć kąt 25 stopni w stosunku do podłogi.'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową, ramiona utrzymuj nieruchomo, łokcie blisko tułowia, prostuj ramiona, mocno napinając triceps. W końcowej fazie ruchu następuje wydech. ',CHAR(10),'2) Powróć do pozycji wyjściowej. ',CHAR(10),'3)Wykonaj założoną liczbę powtórzeń.'),CONCAT('1) Kontroluj ruch podczas trwania ćwiczenia. ',CHAR(10),'2) Stopy solidnie oprzyj o podłogę. ',CHAR(10),'3)Miednica powinna być ustawiona neutralnie przez cały czas trwania ruchu. ',CHAR(10),'4) Bądź skoncentrowany.'),1,'prostowanie-ramion-lezac-na-lawce-z-uzyciem-wyciagu-gornego.mp4',7, '/na-triceps/Prostowanie_ramion_lezac_na_lawce_z_uzyciem_wyciagu_gornego.jpg');
