Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Pompki w podporze tyłem ',CONCAT('1) Dłonie ułożone na podwyższeniu, stopy oparte o podłoże. Kończyny dolne wyprostowane.',CHAR(10),'2) Ciało w podporze tyłem, tułów prostopadle do podłoża.',CHAR(10),'3) Ramiona rozstawione na szerokość barków.'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową, wykonaj wdech, a następnie ugnij przedramiona, opuszczając ciało.',CHAR(10),'2) W dolnej pozycji zatrzymaj ruch na ułamek sekundy, a następnie mocną pracą wyprostną w łokciach unieś ciało i wróć do pozycji początkowej, w końcowej fazie ruchu wykonaj wydech. ',CHAR(10),'3) Wykonaj wyznaczoną liczbę powtórzeń.'),CONCAT('1) Kontroluj ruch podczas trwania ćwiczenia.',CHAR(10),'2) Trzymaj głowę w równej linii z plecami.',CHAR(10),'3) Bądź skoncentrowany.',CHAR(10),'4) Trzymaj mocno napięty brzuch.'),1,'pompki_tylem_ na_krzesle.mp4',7, '/na-triceps/Pompki_w_podporze_tylem_na_laweczce.jpg');
