Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Prostowanie przedramienia w pionie ze sztangielką',CONCAT('1) Stań prosto, ramię trzymające sztangielkę wyprostowane nad głową, prostopadle do podłoża.',CHAR(10),'2) Brzuch i pośladki mocno napięte, ciało ustabilizowane.',CHAR(10),'3) Druga ręka spoczywa na biodrach.'),CONCAT('1) Weź wdech, utrzymując prawidłową pozycję wyjściową ramię utrzymuj nieruchomo, łokieć blisko głowy, powoli obniżaj sztangielkę za głowę do momentu wyczucia silnego rozciągnięcia tricepsa.',CHAR(10),'2) Po zatrzymaniu na ułamek sekundy w najniższym położeniu rozpocznij wyprost w łokciu.',CHAR(10),'3) W końcowej fazie ruchu wykonaj wydech i napnij mocno triceps, wracając do pozycji wyjściowej.'),CONCAT('1) Kontroluj ruch podczas trwania ćwiczenia.',CHAR(10),'2) Staraj się nie wyginać pleców w łuk, uruchom tłocznię brzuszną, która pozwoli na stabilizację sylwetki.',CHAR(10),'3) Bądź skoncentrowany.',CHAR(10),'4) Jeżeli masz problem z utrzymaniem ramienia nad głową, możesz pomóc sobie drugą ręką, trzymając ramię ćwiczące za łokieć.'),1,'prostowanie_przedramienia_w_pionie_ze_sztangielka.mp4',7, '/na-triceps/Prostowanie_przedramienia_w_pionie_ze_sztangielka.jpg');
