Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Prostowanie przedramion ze sztangą łamaną leżąc na ławce płaskiej',CONCAT('1) Leżąc na ławce, chwyć sztangę łamaną wąskim chwytem. Dłonie nieco węziej niż szerokość barków, kciuki skierowane do siebie.',CHAR(10),'2) Wznieś sztangę z uchwytu i unieś ją nad klatkę piersiową ramiona powinny znajdować się prostopadle do podłoża.'),CONCAT('1) Inicjując wdech, opuść sztangę do czoła, trzymaj ramiona nieruchomo.',CHAR(10),'2) Zatrzymaj ruch na ułamek sekundy nad głową. ',CHAR(10),'3) Następnie wykonaj wyprost w łokciach, wracając do pozycji początkowej, w końcowej fazie ruchu wykonaj wydech.'),CONCAT('1) Kontroluj ruch podczas trwania ćwiczenia.',CHAR(10),'2) Stopy solidnie wesprzyj o podłogę.',CHAR(10),'3) Bądź skoncentrowany.',CHAR(10),'4) Opuszczanie do czoła wykonuj ze szczególną ostrożnością.'),1,'prostowanie_przedramion_ze_sztanga_lamana_lezac_na_lawce_plaskiej.mp4',7, '/na-triceps/Prostowanie_przedramion_ze_sztanga_lamana_lezac_na_lawce_plaskiej.jpg');
