Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Prostowanie przedramion ze sztangielką trzymaną oburącz siedząc',CONCAT('1) Pozycja siedząca, ramiona wyprostowane nad głową, prostopadłe do podłoża, dłonie trzymają sztangielkę.',CHAR(10),'2) Plecy proste, uruchomiona tłocznia brzuszna oraz napięty mięsień pośladkowy w celu stabilizacji sylwetki.'),CONCAT('1) Weź wdech, utrzymując prawidłową pozycję wyjściową, ramionautrzymuj nieruchomo, łokcie blisko głowy, powoli obniżaj sztangielkę za głowę do momentu wyczucia silnego rozciągnięcia tricepsa.',CHAR(10),'2) W najniższym punkcie zatrzymaj ruch na ułamek sekundy, następne prostując ramiona i unosząc hantlę, wróć do pozycji wyjściowej, w końcowej fazie ruchu wykonaj wydech.',CHAR(10),'3) Napnij mocno triceps w momencie pełnego wyprostu.'),CONCAT('1) Kontroluj ruch podczas trwania ćwiczenia.',CHAR(10),'2) Utrzymuj łokcie blisko głowy.',CHAR(10),'3) Staraj się nie wyginać pleców w łuk.',CHAR(10),'4) Bądź skoncentrowany.'),2,'prostowanie_przedramion_ze_sztangielka_trzymana_oburacz_siedzac.mp4',7, '/na-triceps/Prostowanie_przedramion_ze_sztangielka_trzymana_oburacz_siedzac.jpg');
