Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Prostowanie ramienia z wykorzystaniem wyciągu górnego',CONCAT('1) Stań przodem do wyciągu. Jedną ręką złap uchwyt tak, aby kciuk był skierowany na zewnątrz (podchwyt).',CHAR(10),'2) Ramię niezaangażowane w ćwiczenie oprzyj o talię bądź, jeśli jest taka możliwość, o ramę wyciągu.',CHAR(10),'3) Łokieć znajduje się blisko tułowia.'),CONCAT('1) Weź wdech, utrzymując prawidłową pozycję wyjściową ramię utrzymuj nieruchomo, łokieć blisko tułowia, prostuj ramię, ściągając uchwyt wyciągu w dół i mocno napinając triceps.',CHAR(10),'2) W końcowej fazie ruchu wykonaj wydech, zatrzymaj ruch, gdy łokieć będzie w pełnym wyproście. ',CHAR(10),'3) Następnie poprzez spokojne i kontrolowane uginanie wróć do pozycji początkowej.'),CONCAT('1) Kontroluj ruch podczas trwania ćwiczenia.',CHAR(10),'2) Utrzymuj łokieć przy ciele.',CHAR(10),'3) Staraj się nie wyginać pleców w łuk.',CHAR(10),'4) Bądź skoncentrowany.'),2,'prostowanie_ramienia_jednoracz_z_wyciagu_gornego.mp4',7, '/na-triceps/Prostowanie_ramienia_z_wykorzystaniem_wyciagu_gornego.jpg');
