Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Prostowanie ramion z gryfem trzymanym podchwytem z wyciągu górnego',CONCAT('1) Stań przodem do wyciągu. Chwyć oburącz za gryf podchwytem (kciuki skierowane na zewnątrz).',CHAR(10),'2) Sylwetka wyprostowana. Plecy proste, brzuch i pośladki napięte.',CHAR(10),'3) Łokcie znajdują się blisko tułowia. Ramiona prostopadle do ziemi.'),CONCAT('1) Weź wdech, utrzymując prawidłową pozycję wyjściową ramionautrzymuj nieruchomo, łokcie blisko tułowia, prostuj ramiona, ściągnij uchwyt wyciągu w dół i mocno napnij triceps aż do pełnego wyprostu ramion, w końcowej fazie ruchu wykonaj wydech.',CHAR(10),'2) W momencie maksymalnego napięcia zatrzymaj ruch na ułamek sekundy.',CHAR(10),'3) Następnie powolnym i kontrolowanym ruchem zacznij uginać ramię rozciągając triceps, wróć do pozycji wyjściowej.'),CONCAT('1) Kontroluj ruch podczas trwania ćwiczenia.',CHAR(10),'2) Łokcie utrzymuj na szerokości chwytu.',CHAR(10),'3) Staraj się nie wyginać pleców w łuk.',CHAR(10),'4) Bądź skoncentrowany.'),2,'prostowanie_ramion_z_gryfem_trzymanym_podchwytem_z_wyciagu_gornego.mp4',7, '/na-triceps/Prostowanie_ramion_z_gryfem_trzymanym_podchwytem_z_wyciagu_gornego.jpg');
