Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Wąskie pompki na triceps. Diamentowe pompki',CONCAT('1) Podpór przodem. Dłonie ustawione wąsko przy sobie w taki sposób, by między nimi powstał trójkąt (diament). ',CHAR(10),'2) Ciało w jednej linii, brzuch oraz pośladki mocno napięte.'),CONCAT('1) Weź wdech, utrzymując prawidłową pozycję wyjściową rozpocznij uginanie ramion, opuszczając klatkę piersiową w stronę dłoni.',CHAR(10),'2) Nie kładź się na podłogę, w najniższym punkcie wykonaj zatrzymanie na ułamek sekundy, po czym energiczną pracą ramion wróć do pozycji początkowej, wykonaj równocześnie wydech. ',CHAR(10),'3) Wykonaj ruch wyznaczoną liczbę powtórzeń.'),CONCAT('1) Kontroluj ruch podczas trwania ćwiczenia.',CHAR(10),'2) Stopy solidnie oprzyj o podłogę.',CHAR(10),'3) Bądź skoncentrowany.',CHAR(10),'4) Napinając mięśnie brzucha oraz pośladkowe, zachowaj prawidłową (wyprostowaną) postawę.'),2,'waskie_pompki_na_triceps_diamentowe_pompki.mp4',7, '/na-triceps/Waskie_pompki_na_triceps._Diamentowe_pompki.jpg');
