Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Wyciskanie sztangi łamanej wąskim chwytem na ławeczce płaskiej',CONCAT('1) Pozycja leżąca na ławeczce, ramiona wyprostowane, sztanga trzymana nachwytem nad mostkiem, kciuki skierowane do wewnątrz. ',CHAR(10),'2) Brzuch i pośladki mocno napięte, ciało ustabilizowane.'),CONCAT('1) Weź wdech, utrzymując prawidłową pozycję wyjściową rozpocznij uginanie ramion, opuszczając sztangę do klatki piersiowej na wysokość mostka.',CHAR(10),'2) W dolnej pozycji zatrzymaj ruch na ułamek sekundy, a następnie energicznym ruchem wyciśnij sztangę nad klatkę, w końcowej fazie ruchu wykonaj wydech.',CHAR(10),'3) Wykonaj wyznaczoną liczbę powtórzeń.'),CONCAT('1) Kontroluj ruch podczas trwania ćwiczenia.',CHAR(10),'2) Stopy solidnie zaprzyj o podłogę.',CHAR(10),'3) Bądź skoncentrowany.',CHAR(10),'4) Upewnij się, że podczas ruchu prowadzisz łokcie blisko tułowia, tak aby maksymalnie zaangażować mięśnie trójgłowe ramienia.',CHAR(10),'5) Opuszczanie sztangi powinno trwać dwa razy dłużej niż jej wyciskanie.',CHAR(10),'6) Jeśli wykonujesz to ćwiczenie po raz pierwszy, poproś kogoś o asekurację.'),2,'wyciskanie_sztangi_lamanej_waskim_chwytem_na_laweczce_plaskiej.mp4',7, '/na-triceps/Wyciskanie_sztangi_lamanej_waskim_chwytem_na_laweczce_plaskiej.jpg');
