Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Pompka z gumą',CONCAT('1) Pozycja podporu przodem. ',CHAR(10),'2) Dłonie ustawione na wysokości barków, nieco szerzej niż na szerokość barków, palce dłoni skierowane do przodu. Ramiona zablokowane w stawach łokciowych. ',CHAR(10),'3) Guma zablokowana pod dłońmi i zapleciona za plecami. ',CHAR(10),'4) Mięśnie brzucha i mięśnie pośladkowe napięte.'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową, dokładnie, płynnie i wolno opuszczaj klatkę piersiową (bez dotykania podłoża), równocześnie wykonaj wdech powietrza, następnie dynamiczne unieś tułów i wykonaj wydech powietrza. ',CHAR(10),'2) Wykonaj zadaną liczbę powtórzeń.'),CONCAT('1) Pamiętaj o utrzymywaniu napięcia mięśni brzucha i mięśni pośladkowych podczas całego ruchu. ',CHAR(10),'2) Cały czas kontroluj ruch opuszczania tułowia. ',CHAR(10),'3) Dynamicznie i mocno podnoś tułów. ',CHAR(10),'4) Głowa powinna stanowić przedłużenie kręgosłupa (wzrok skierowany na podłoże).'),1,'pompka_z_guma.mp4',1, '/domowe/Pompka_z_guma.jpg');
