Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Pompki z podparciem kolan',CONCAT('1) Przyjmij pozycję podporu na kolanach.',CHAR(10),'2) Stopy pozostaw na podłodze, dłonie rozstaw szerzej niż na szerokość barków.',CHAR(10),'3)Utrzymuj napięcia brzucha i neutralne ustawienie miednicy.',CHAR(10),'4) Pilnuj, aby łokcie nie były w przeproście.'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową, wykonaj dokładne, płynne i powolne opuszczenie klatki piersiowej (bez dotykania jej o podłoże), połączone z wdechem powietrza.',CHAR(10),'2) Następnie dynamiczne wróć tułowiem do góry poprzez mocną pracę ramion i klatki piersiowej wraz z wydechem powietrza.',CHAR(10),'3) Wykonaj zadaną liczbę powtórzeń.'),CONCAT('1) Pamiętaj o prostych plecach oraz poprawnej pozycji miednicy. ',CHAR(10),'2) Cały czas kontroluj ruch opuszczania tułowia.',CHAR(10),'3) Dynamicznie i mocno kieruj tułów do góry.',CHAR(10),'4) Utrzymuj głowę w równej linii z plecami.',CHAR(10),'5) Bądź skoncentrowany.',CHAR(10),'6) Utrzymuj dłonie szerzej niż szerokość barków.',CHAR(10),'7) Nie śpiesz się podczas wykonywania tego ćwiczenia, zbyt szybkie wykonanie jest błędem technicznym.'),1,'pompki_z_podparciem_kolan.mp4',1, '/domowe/Pompki_z_podparciem_kolan.jpg');
