Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Naprzemienne przyciąganie kolan do klatki w podporze przodem',CONCAT('1) Podpór przodem.',CHAR(10),'2) Tułów wyprostowany, ręce ustawione prostopadle do osi ciała, dłonie w jednej linii, ustawione równolegle do siebie, nieco szerzej niż rozstaw barków.',CHAR(10),'3) Nogi wyprostowane, stopy złączone.',CHAR(10),'4) Stopy mocno zaparte o podłoże.'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową, uruchamiając tłocznię brzuszną, przyciągaj dynamicznie jedno z kolan do klatki piersiowej i wykonaj wydech powietrza.',CHAR(10),'2) Cofnij nogę i jednocześnie przeskocz drugą w stronę klatki piersiowej.',CHAR(10),'3) Wykonaj wyznaczoną liczbę powtórzeń.'),CONCAT('1) Kontroluj ruch podczas trwania ćwiczenia.',CHAR(10),'2) Kolana przyciągaj na przemian.',CHAR(10),'3) Bądź skoncentrowany.',CHAR(10),'4) Napinaj mięśnie brzucha i mięśnie pośladkowe podczas ruchu w celu ustabilizowania pozycji. ',CHAR(10),'5) Pamiętaj o prawidłowym oddychaniu.',CHAR(10),'6) Ćwiczenie wykonuj dynamicznie.'),1,'naprzemienne_przyciaganie_kolan_do_klatki_w_podporze_przodem.mp4',1, '/domowe/Naprzemienne_przyciaganie_kolan_do_klatki_w_podporze_przodem.jpg');
