Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Eksplozywne pompki z dotknięciem kolan',CONCAT('1) Podpór przodem. ',CHAR(10),'2) Tułów wyprostowany, ręce ustawione prostopadle do osi ciała, dłonie w jednej linii, ustawione równolegle do siebie, nieco szerzej niż rozstaw barków. ',CHAR(10),'3) Nogi wyprostowane, stopy złączone. ',CHAR(10),'4) Stopy mocno zaparte o podłoże.'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową, wykonaj płynny wdech, zginając przedramiona i opuszczając klatkę piersiową do podłoża. Należy zadbać o to, aby tułów był cały czas w linii prostej, a biodra nie opadały. ',CHAR(10),'2) Następnie wybij się w górę, dynamicznie prostując ramiona oraz odrywając dłonie od podłoża, po czym unosząc biodra w górę, przyciągnij kolana w stronę klatki piersiowej i szybko dotknij ich dłońmi. ',CHAR(10),'3) Opadaj, ponownie uzyskując cztery punkty podparcia. Wykonaj kolejną pompkę. ',CHAR(10),'4) Powtarzaj ruch zadaną ilość razy.'),CONCAT('1) Kontroluj ruch podczas trwania ćwiczenia. ',CHAR(10),'2) Nogi pozostają wyprostowane przez cały czas. ',CHAR(10),'3) Bądź skoncentrowany. ',CHAR(10),'4) Napinając mięśnie brzucha oraz pośladkowe, zachowuj prawidłową (wyprostowaną) postawę ciała. ',CHAR(10),'5) Pamiętaj o prawidłowym oddychaniu.'),1,'',1, '/domowe/Eksplozywne_pompki_z_dotknieciem_kolan.jpg');
