Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Pompki do pozycji T',CONCAT('1) Podpór przodem.',CHAR(10),'2) Tułów wyprostowany, ręce ustawione prostopadle do osi ciała, dłonie w jednej linii, ustawione równolegle do siebie, nieco szerzej niż rozstaw barków.',CHAR(10),'3) Nogi wyprostowane, stopy złączone.',CHAR(10),'4) Brzuch oraz pośladki mocno napięte.'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową, wykonaj wdech, a następnie zginając ramiona w łokciach, obniż całe ciało (wykonaj pompkę). Należy zadbać o to, aby tułów był cały czas w linii prostej, a biodra nie opadały.',CHAR(10),'2) Wróć do pozycji wyjściowej, prostując ramiona.',CHAR(10),'3) Następnie przenosząc ciężar ciała na jedno z wyprostowanych ramion, otwieraj postawę, ustaw stopy na bok i unieś drugie ramię w górę, tak aby ciało przypominało literę T. Wytrzymaj w tej pozycji przez sekundę i wykonaj wydech.',CHAR(10),'4) Wróć do podporu przodem i wykonaj kolejną pompkę.'),CONCAT('1) Kontroluj ruch podczas trwania ćwiczenia.',CHAR(10),'2) Podczas wykonywania pozycji „T” bardzo mocno napnij mięśnie pośladkowe oraz proste brzucha tak, aby całe ciało pozostało w jednej linii, a biodra nie opadały.',CHAR(10),'3) Bądź skoncentrowany.',CHAR(10),'4) Pamiętaj o prawidłowym oddychaniu.'),1,'pompki_do_pozycji_t.mp4',1, '/domowe/Pompki_do_pozycji_T.jpg');
