Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Pompki eksplozywne',CONCAT('1) Podpór przodem.',CHAR(10),'2) Tułów wyprostowany, ręce ustawione prostopadle do osi ciała, dłonie w jednej linii, ustawione równolegle do siebie, na szerokość barków.',CHAR(10),'3) Nogi wyprostowane przez cały czas, stopy lekko rozstawione.',CHAR(10),'4) Pilnuj, aby stopy były mocno zaparte o podłoże.'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową, wykonaj wdech, zginając przedramiona i opuszczając klatkę piersiową do podłoża. Należy zadbać o to, aby tułów był cały czas w linii prostej, a biodra nie opadały.',CHAR(10),'2) Następnie wybij się dynamicznie w górę, silnie prostując ramiona, wykonaj mocny wydech, po czym opadaj, płynnie przechodząc do kolejnej pompki.',CHAR(10),'3) Wykonaj ruch wyznaczoną liczbę powtórzeń.'),CONCAT('1) Kontroluj ruch podczas trwania ćwiczenia.',CHAR(10),'2) Nogi pozostają wyprostowane przez cały czas.',CHAR(10),'3) Bądź skoncentrowany.',CHAR(10),'4) Napinając mięśnie brzucha oraz pośladkowe, zachowuj prawidłową (wyprostowaną) postawę ciała.',CHAR(10),'5) Pamiętaj o prawidłowym oddychaniu.',CHAR(10),'6) Na początku warto wykonywać to ćwiczenie na miękkim podłożu, które zapewnia amortyzację w razie niechcianego upadku.'),1,'pompki_eksplozywne.mp4',1, '/domowe/Pompki_eksplozywne.jpg');
