Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Pompki na podwyższeniu',CONCAT('1) Podpór przodem na stepie bądź innym rodzaju podwyższenia.',CHAR(10),'2) Tułów wyprostowany, ręce ustawione prostopadle do osi ciała, dłonie w jednej linii, ustawione równolegle do siebie, nieco szerzej niż rozstaw barków.',CHAR(10),'3) Nogi wyprostowane, stopy złączone.'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową, wykonaj wdech, a następnie ugnij ramiona w łokciach i opuść klatkę piersiową do podwyższenia. Należy zadbać o to, aby tułów był cały czas w linii prostej, a biodra nie opadały.',CHAR(10),'2) Wróć do pozycji wyjściowej, prostując ramiona i w końcowej fazie ruchu wykonując wydech. ',CHAR(10),'3) Wykonaj ruch wyznaczoną liczbę powtórzeń.'),CONCAT('1) Kontroluj ruch podczas trwania ćwiczenia.',CHAR(10),'2) Bądź skoncentrowany.',CHAR(10),'3) Napinając mięśnie brzucha oraz pośladkowe, zachowuj prawidłową (wyprostowaną) postawę.',CHAR(10),'4) Upewnij się, że przedmiot służący za podparcie jest solidnie unieruchomiony.'),1,'pompki_na_podwyzszeniu.mp4',1, '/domowe/Pompki_na_podwyzszeniu.jpg');
