Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Pompki z przyciągnięciem kolana do klatki piersiowej',CONCAT('1) Podpór przodem.',CHAR(10),'2) Tułów wyprostowany, ręce ustawione prostopadle do osi ciała, dłonie w jednej linii, ustawione równolegle do siebie, nieco szerzej niż rozstaw barków.',CHAR(10),'3) Nogi wyprostowane, stopy złączone.'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową, wykonaj wdech, następnie zginając ramiona w łokciach, opuść klatkę piersiową do podłoża. Należy zadbać o to, aby tułów był cały czas w linii prostej, a biodra nie opadały.',CHAR(10),'2) Prostując ramiona, wróć do pozycji początkowej.',CHAR(10),'3) Następnie przyciągnij jedno z kolan do klatki piersiowej, wykonując wydech powietrza.',CHAR(10),'4) Powróć do podporu przodem.',CHAR(10),'5) Wykonaj ruch wyznaczoną liczbę powtórzeń.'),CONCAT('1) Kontroluj ruch podczas trwania ćwiczenia.',CHAR(10),'2) Kolana przyciągaj na przemian.',CHAR(10),'3) Bądź skoncentrowany.',CHAR(10),'4) Napinając mięśnie brzucha oraz pośladkowe, zachowuj prawidłową (wyprostowaną) postawę.',CHAR(10),'5) Pamiętaj o prawidłowym oddychaniu.',CHAR(10),'6) Łokcie prowadź na zewnątrz, nie powinny dotykać tułowia.'),1,'',1, '/domowe/Pompki_z_przyciagnieciem_kolana_do_klatki_piersiowej.jpg');
