Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Pompki spidermana',CONCAT('1) Podpór przodem.',CHAR(10),'2) Tułów wyprostowany, ręce ustawione prostopadle do osi ciała, dłonie w jednej linii, ustawione równolegle do siebie, nieco szerzej niż rozstaw barków.',CHAR(10),'3) Nogi wyprostowane, stopy złączone.'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową, wykonaj wdech, następnie zginając ramiona w łokciach, opuść klatkę piersiową do podłoża. Należy zadbać o to, aby tułów był cały czas w linii prostej, a biodra nie opadały.',CHAR(10),'2) Prostując ramiona wróć do pozycji wyjściowej, w końcowej fazie ruchu wykonaj wydech.',CHAR(10),'3) Wykonaj ruch wyznaczoną liczbę powtórzeń.'),CONCAT('1) Kontroluj ruch podczas trwania ćwiczenia.',CHAR(10),'2) Stopy oprzyj o podłogę.',CHAR(10),'3) Bądź skoncentrowany.',CHAR(10),'4) Napinając mięśnie brzucha oraz pośladkowe, zachowuj prawidłową (wyprostowaną) postawę.',CHAR(10),'5) Pamiętaj o prawidłowym oddychaniu.'),1,'pompki_tradycyjne_pompka_klasyczna.mp4',1, '/domowe/Pompki_tradycyjne.jpg');
