Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Naprzemienne wznosy ręki i nogi w klęku podpartym',CONCAT('1) Klęk podparty.',CHAR(10),'2) Tułów wyprostowany, ręce ustawione prostopadle do osi ciała, dłonie w jednej linii, ustawione równolegle do siebie na rozstaw barków. ',CHAR(10),'3) Kolana na szerokości bioder.'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową, weź wdech uruchamiając tłocznię brzuszną, unieś wyprostowane ramię oraz przeciwległą wyprostowaną nogę, tak aby znalazły się równolegle do podłoża.',CHAR(10),'2) Wytrzymaj w takiej pozycji 1–2 sekundy, po czym z wydechem wróć do pozycji wyjściowej i unieś przeciwne ramię oraz przeciwną nogę.',CHAR(10),'3) Wykonaj ćwiczenie zadaną liczbę powtórzeń.'),CONCAT('1) Kontroluj ruch podczas trwania ćwiczenia.',CHAR(10),'2) Bądź skoncentrowany.',CHAR(10),'3) Utrzymuj napięcie mięśni brzucha oraz pośladków.',CHAR(10),'4) Pamiętaj o prawidłowym oddychaniu.',CHAR(10),'5) Ćwiczenie wykonuj powoli, z pełnym zaangażowaniem.',CHAR(10),'6) Utrzymuj biodra na jednej wysokości podczas podnoszenia kończyn.'),1,'naprzemienne_wznosy_reki_i_nogi_w_kleku_podpartym.mp4',2, '/domowe/Naprzemienne_wznosy_reki_i_nogi_w_kleku_podpartym.jpg');
