Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Deska/ścianka/plank',CONCAT('1) Podpór przodem na przedramionach, łokcie pod barkami, nogi złączone, wyprostowane, ustawione w jednej linii z tułowiem – równolegle do podłoża.',CHAR(10),'2) Zachowana naturalna krzywizna kręgosłupa. Wzrok skierowany w przód.',CHAR(10),'3) Pozycję utrzymujemy do czasu, kiedy jesteśmy w stanie kontrolować ustawienie ciała.',CHAR(10),'4) Tylko utrzymanie prawidłowej pozycji będzie miało wpływ na pozytywne efekty.'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową i napinając mocno mięśnie nóg, pośladki oraz brzuch, postaraj się wytrzymać jak najdłużej, wykonaj izometryczny skurcz mięśni oraz głęboko oddychaj.'),CONCAT('1) Mięśnie brzucha oraz pośladkowe powinny być napięte przez cały czas trwania ćwiczenia.',CHAR(10),'2) Dolny odcinek pleców nie powinien być nadmiernie wygięty. ',CHAR(10),'3) Oddychaj podczas utrzymywania izometrycznego skurczu.',CHAR(10),'4) W celu zwiększenia intensywności ćwiczenia unieś nieznacznie jedną nogę lub użyj obciążenia.'),2,'deska_scianka_plank.mp4',2, '/domowe/Deska_scianka_plank.jpg');
