Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Przysiad przy ścianie',CONCAT('1) Stopy ustawione na szerokość barków, całą powierzchnią dociśnięte do podłogi. ',CHAR(10),'2) Plecy proste, tułów napięty, głowa w pozycji naturalnej, wzrok skierowany do przodu.',CHAR(10),'3) Łopatki ściągnięte, klatka piersiowa wypięta do przodu.'),CONCAT('1) Opierając się o ścianę, wykonaj przysiad do momentu, aż twoje uda będą równoległe do podłoża.',CHAR(10),'2) Dociśnij odcinek lędźwiowy do ściany i napnij mocno brzuch.',CHAR(10),'3) Oddychaj głęboko i postaraj się wytrzymać określony czas.'),CONCAT('1) Podczas całego ćwiczenia utrzymuj tułów napięty.',CHAR(10),'2) Nie pozwól, aby kolana schodziły się do środka.',CHAR(10),'3) Dociskaj całą stopę do podłoża.',CHAR(10),'4) Pamiętaj o głębokim oddychaniu.'),2,'przysiad_przy_scianie.mp4',2, '/domowe/Przysiad_przy_scianie.jpg');
