Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Rowerek',CONCAT('1) Pozycja leżąca na plecach. Ramiona ugięte za głową. Głowa lekko podtrzymywana dłońmi.',CHAR(10),'2) Nogi ugięte w kolanach pod kątem 90 stopni. ',CHAR(10),'3) Odcinek lędźwiowy kręgosłupa dociśnięty do podłogi.'),CONCAT('1) Wyprostuj prawą nogę, jednocześnie przyciągnij lewe kolano do prawego łokcia i skręć tułów tak, aby możliwe było wykonanie ruchu. Wykonaj wydech.',CHAR(10),'2) Przez ułamek sekundy zatrzymaj ruch w maksymalnym napięciu mięśni brzucha.',CHAR(10),'3) Wróć do pozycji wyjściowej, wykonaj płynny wdech.',CHAR(10),'4) Wykonaj ruch na przeciwną stronę.',CHAR(10),'5) Wykonaj wyznaczoną liczbę powtórzeń.'),CONCAT('1) Mięśnie brzucha powinny być mocno napięte przez cały czas trwania ćwiczenia.',CHAR(10),'2) Ćwiczenie wykonuj powoli, z pełnym wyczuciem zaangażowanej grupy mięśniowej.',CHAR(10),'3) Skup się podczas wykonywania ruchu.',CHAR(10),'4) Zwróć uwagę na prawidłowe oddychanie.'),2,'rowerek.mp4',2, '/domowe/Rowerek.jpg');
