Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Nożyczki nogami',CONCAT('1) Pozycja leżąca na plecach. Ramiona wyprostowane wzdłuż tułowia.',CHAR(10),'2) Nogi wyprostowane, ustawione równolegle do podłoża.',CHAR(10),'3) Odcinek lędźwiowy kręgosłupa dotyka maty. Głowa lekko uniesiona.'),CONCAT('1) Zachowując prawidłową postawę ciała, unieś wyprostowane nogi z podłoża, pamiętając, aby odcinek lędźwiowy był silnie dociśnięty do podłoża.',CHAR(10),'2) Odwiedź równocześnie nogi na zewnątrz, po czym przyciągnij je do siebie, ustaw jedną nogę pod drugą, po czym ponownie je rozszerz.',CHAR(10),'3) Wykonuj nożyce naprzemiennie.',CHAR(10),'4) Powtarzaj ruch do momentu odczucia silnego pieczenia w mięśniach brzucha.'),CONCAT('1) Mięśnie brzucha powinny być mocno napięte przez cały czas trwania ćwiczenia.',CHAR(10),'2) Ćwiczenie wykonuj powoli z pełnym wyczuciem zaangażowanej grupy mięśniowej.',CHAR(10),'3) Skup się podczas wykonywania ruchu.',CHAR(10),'4) Zwróć uwagę na prawidłowe oddychanie.'),2,'nożyczki_nogami.mp4',2, '/domowe/Nozyczki_nogami.jpg');
