Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Unoszenie bioder w górę w podporze bokiem',CONCAT('1) Pozycja leżąca na boku, ciało oparte na przedramieniu. Łokieć ustawiony pod barkiem. ',CHAR(10),'2) Noga na nodze, kolana wyprostowane.'),CONCAT('1) Unieś biodra tak, aby tułów był w jednej linii z nogami.',CHAR(10),'2) Napnij mocno mięśnie brzucha oraz mięśnie pośladkowe – wytrzymaj w tej pozycji określony czas.',CHAR(10),'3) Następnie ćwiczenie wykonaj na drugim boku.'),CONCAT('1) Mięśnie brzucha powinny być mocno napięte przez cały czas trwania ćwiczenia.',CHAR(10),'2) Oddychaj, gdy mięśnie pozostają w skurczu izometrycznym.',CHAR(10),'3) Postaraj się trzymać tułów w linii. Nie cofaj ani nie wypychaj nadmiernie bioder.'),1,'unoszenie_bioder_w_gore_w_podporze_bokiem.mp4',2, '/domowe/Unoszenie_bioder_w_gore_w_podporze_bokiem.jpg');
