Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Deseczka bokiem – side plank',CONCAT('1) Podpór bokiem na przedramieniu, ramię ugięte pod kątem 90 stopni, górna część prostopadle do podłoża. Kończyny dolne złączone, wyprostowane, w jednej linii z tułowiem i ułożone równolegle do podłoża.',CHAR(10),'2) Plecy proste. Wzrok skierowany w przód.',CHAR(10),'3) Mięśnie brzucha mocno napięte.'),CONCAT('1)Utrzymuj prawidłową pozycję wyjściową, napinaj mocno mięśnie nóg, pośladki oraz brzuch, utrzymaj pozycję przez wyznaczony czas, wykonaj izometryczny skurcz mięśni oraz oddychaj głęboko.'),CONCAT('1) Mięśnie brzucha powinny być mocno napięte przez cały czas trwania ćwiczenia.',CHAR(10),'2) Dolny odcinek pleców nie powinien być wygięty. Odciąża to pracę mięśni brzucha i sprawia, że ćwiczenie staje się mniej intensywne.',CHAR(10),'3) W celu zwiększenia intensywności ćwiczenia możesz unieść jedną nogę bądź oprzeć się na dłoni z wyprostowanym ramieniem.',CHAR(10),'4) Oddychaj głęboko podczas utrzymywania izometrycznego skurczu.'),2,'deseczka_bokiem_side_plank.mp4',2, '/domowe/Deseczka_bokiem_–_side_plank.jpg');
