Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Naprzemianstronne przyciąganie kolan do łokci w podporze',CONCAT('1)Podpór przodem, ramiona wyprostowane, ustawione prostopadle do podłoża.'),CONCAT('1) Utrzymując prawidłową pozycję ciała, weź wdech, przyciągnij kolano pod klatkę piersiową w kierunku przeciwnego łokcia.',CHAR(10),'2) Zatrzymaj spięcie na ułamek sekundy, po czym wróć do pozycji początkowej, wykonaj wydech. ',CHAR(10),'3) Następnie wykonaj ćwiczenie drugą stroną.'),CONCAT('1) Wykonuj ruch w wolnym tempie.',CHAR(10),'2) Bądź skoncentrowany.',CHAR(10),'3) Pamiętaj o prawidłowym oddychaniu.',CHAR(10),'4) Postaraj się znaleźć właściwe napięcie mięśniowe.',CHAR(10),'5) Pamiętaj o mocnym spięciu brzucha.',CHAR(10),'6) Głowa powinna pozostać w jednej linii z plecami, a wzrok skierowany w dół.'),1,'naprzemienne_przyciaganie_kolan_do_lokci_w_podporze.mp4',2, '/domowe/Naprzemianstronne_przyciaganie_kolan_do_lokci_w_podporze.jpg');
