Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Nurkowanie delfina',CONCAT('1) Podpór przodem na przedramionach, ramiona ugięte pod kątem 90 stopni, górna część prostopadle do podłoża. Kończyny dolne złączone, wyprostowane, w jednej linii z tułowiem i równoległe do podłoża.',CHAR(10),'2) Plecy proste. Wzrok skierowany w przód.',CHAR(10),'3) Mięśnie brzucha mocno napięte.'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową, napnij mocno mięśnie brzucha, skieruj biodra w górę, a następnie powoli opuść.',CHAR(10),'2) Wykonaj wyznaczoną liczbę powtórzeń.'),CONCAT('1) Mięśnie brzucha powinny być mocno napięte przez cały czas trwania ćwiczenia.',CHAR(10),'2) Staraj się kontrolować ułożenie miednicy w celu silnego dopięcia mięśni brzucha.',CHAR(10),'3) Oddychaj podczas utrzymywania izometrycznego skurczu.'),2,'nurkowanie_delfina.mp4',2, '/domowe/Nurkowanie_delfina.jpg');
