Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Unoszenie bioder ze stopami ustawionymi na podwyższeniu',CONCAT('1) Połóż się na plecach. Nogi ustaw ugięte w kolanach, lekko rozstawione, stopy połóż na podwyższeniu (krzesło, stolik, kanapa).',CHAR(10),'2) Ręce na biodrach.'),CONCAT('1) Utrzymując prawidłową pozycję ciała, weź wdech i napnij mięśnie brzucha, następnie poprzez ruch bioder w górę unieś ciało do pełnego wyprostu w biodrach.',CHAR(10),'2) Zatrzymaj ruch na ułamek sekundy w pełnym wyproście. ',CHAR(10),'3) Wolno wróć do pozycji wyjściowej, obniż biodra i wykonaj wydech.'),CONCAT('1) Wykonuj ruch w wolnym tempie.',CHAR(10),'2) Bądź skoncentrowany.',CHAR(10),'3) Pamiętaj o prawidłowym oddychaniu.',CHAR(10),'4) Postaraj się znaleźć właściwe napięcie mięśni pośladkowych.',CHAR(10),'5) Unoszenie bioder wykonuj wysoko do momentu, aż tułów znajdzie się w jednej linii z udami, a pośladki będą maksymalnie napięte.'),1,'unoszenie_bioder_ze_stopami_ustawionymi_na_podwyzeszniu.mp4',4, '/domowe/Unoszenie_bioder_ze_stopami_ustawionymi_na_podwyzszeniu.jpg');
