Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Wykroki w miejscu',CONCAT('1) Sylwetka wyprostowana.',CHAR(10),'2) Mocno napięte mięśnie brzucha.',CHAR(10),'3) Łopatki ściągnięte, brzuch i pośladki napięte.'),CONCAT('1) Weź wdech, utrzymując prawidłową pozycję wyjściową oraz naturalną krzywiznę kręgosłupa, wykonaj wykrok jedną nogą tak, aby staw kolanowy nogi wykrocznej był zgięty w kącie ok. 90 stopni. Noga zakroczna pozostaje w miejscu, przejdź na palce podczas wykonywania wykroku.',CHAR(10),'2) Wykonaj wydech, wróć dynamicznie do pozycji wyjściowej.',CHAR(10),'3) Wykonaj wyznaczoną liczbę powtórzeń, zmieniając nogę wykroczną.'),CONCAT('1) Kontroluj ruch wykroku.',CHAR(10),'2) Nie bujaj tułowiem (trzymaj stabilnie w jednej linii).',CHAR(10),'3) Wzrok skieruj przed siebie.',CHAR(10),'4) Bądź skoncentrowany.',CHAR(10),'5) Nie prowadź kolan do wewnątrz (utrzymuj je skierowane lekko na zewnątrz).'),1,'wykroki_w_miejscu.mp4',4, '/domowe/Wykroki_w_miejscu.jpg');
